import { Fireworks } from '@fireworks-js/react'

export default function App() {
  return (
    <Fireworks
      options={{
        autoresize: true,
        opacity: 0.5,
        acceleration: 1.0,
        friction: 1.0,
        gravity: 1.5,
        particles: 50,
        traceSpeed: 10,
        explosion: 10,
        intensity: 30,
        flickering: 50,
        lineStyle: 'round',
        delay: {
          min: 50,
          max: 60
        },
        hue: {
          min: 0,
          max: 360
        },
        rocketsPoint: {
          min: 40,
          max: 60
        },
        lineWidth: {
          explosion: {
            min: 9,
            max: 10
          },
          trace: {
            min: 1,
            max: 2
          }
        },
        brightness: {
          min: 50,
          max: 80
        },
        decay: {
          min: 0.010,
          max: 0.015
        },
        mouse: {
          click: false,
          move: false,
          max: 1
        }
      }}
      style={{
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        position: 'fixed',
        backgroundImage: `url(${require("./Sky.jpg")})`,
        //https://images.unsplash.com/photo-1619045119136-349759036541?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1974&q=80
        background: '#000'

      }}
    />
  )
}


